             document.addEventListener("DOMContentLoaded", function () {

            function processExternalLinks(scope = document) {
              scope.querySelectorAll('.post-outer a[href]').forEach(link => {

                // Skip if already processed
                if (link.dataset.extChecked) return;

                const href = link.getAttribute("href");

                // Skip invalid / special links
                if (!href || 
                    href.startsWith("#") || 
                    href.startsWith("mailto:") || 
                    href.startsWith("tel:") ||
                    href.startsWith("javascript:")
                ) return;

                try {
                  const url = new URL(link.href);

                  if (url.hostname !== location.hostname) {
                    link.target = "_blank";
                    link.rel = "noopener noreferrer";
                  }

                  link.dataset.extChecked = "true"; // mark processed

                } catch (e) {}
              });
            }

            // Run on first load
            processExternalLinks();

            // OPTIONAL: If infinite scroll / AJAX enabled
            const observer = new MutationObserver(mutations => {
              mutations.forEach(mutation => {
                mutation.addedNodes.forEach(node => {
                  if (node.nodeType === 1) {
                    processExternalLinks(node);
                  }
                });
              });
            });

            observer.observe(document.body, {
              childList: true,
              subtree: true
            });

