          let players = new Map();
          let observer;

          // Load YouTube Iframe API
            const tag = document.createElement('script');
            tag.src = "https://www.youtube.com/iframe_api";
            document.body.appendChild(tag);

            function onYouTubeIframeAPIReady() {
              setupObserver();
              initAllVideos();
              watchForNewVideos();
            }

            function setupObserver() {
              observer = new IntersectionObserver(handleIntersect, {
                threshold: 0.5
              });
            }

            function initAllVideos() {
              document.querySelectorAll('iframe').forEach(processIframe);
            }

            function processIframe(iframe) {
              if (!isYouTubeIframe(iframe)) return;
              if (players.has(iframe)) return;

              autoEnableJSAPI(iframe);

              const player = new YT.Player(iframe, {
                events: {
                  onStateChange: (e) => {
                    if (e.data === YT.PlayerState.PLAYING) {
                      pauseOtherVideos(player);
                    }
                  }
                }
              });

              players.set(iframe, player);
              observer.observe(iframe);
            }

            function handleIntersect(entries) {
              entries.forEach(entry => {
                const player = players.get(entry.target);
                if (!player) return;

                if (!entry.isIntersecting) {
                  player.pauseVideo();
                }
              });
            }

            function pauseOtherVideos(activePlayer) {
              players.forEach(player => {
                if (player !== activePlayer) {
                  player.pauseVideo();
                }
              });
            }

            function isYouTubeIframe(iframe) {
              return /youtube\.com|youtu\.be/.test(iframe.src);
            }

            function autoEnableJSAPI(iframe) {
              const url = new URL(iframe.src);

              if (!url.searchParams.has('enablejsapi')) {
                url.searchParams.set('enablejsapi', '1');
                iframe.src = url.toString();
              }
            }

          // Infinite scroll / AJAX support
            function watchForNewVideos() {
              const mo = new MutationObserver(mutations => {
                mutations.forEach(mutation => {
                  mutation.addedNodes.forEach(node => {
                    if (node.nodeType !== 1) return;

                    if (node.tagName === 'IFRAME') {
                      processIframe(node);
                    }

                    node.querySelectorAll?.('iframe').forEach(processIframe);
                  });
                });
              });

              mo.observe(document.body, {
                childList: true,
                subtree: true
              });
            }


          //Pause when tab loses focus
            document.addEventListener('visibilitychange', () => {
            if (document.hidden) {
              players.forEach(p => p.pauseVideo());
            }
          });

			/*==========================================
			 Fixes aspect ratio automatically
             Detects YouTube Shorts (9:16)
             Supports YouTube, Facebook, Instagram, X, Google Drive
             Works with infinite scroll
             Prevents double-processing
             Mobile optimized
             Centers vertical videos nicely
