        (function () {

          function isYouTubeShorts(iframe) {
            return iframe.src.includes("/shorts/") ||
                   (iframe.getAttribute("height") && iframe.getAttribute("width") &&
                    parseFloat(iframe.getAttribute("height")) >
                    parseFloat(iframe.getAttribute("width")));
          }

          function fixVideoAspectRatio(iframe) {

            if (iframe.dataset.aspectFixed) return;
            iframe.dataset.aspectFixed = "true";

            let width = parseFloat(iframe.getAttribute("width"));
            let height = parseFloat(iframe.getAttribute("height"));

            let ratio = "16 / 9";
            let isVertical = false;

            if (width && height && width > 0 && height > 0) {
              ratio = `${width} / ${height}`;
              if (height > width) {
                isVertical = true;
              }
            }

            if (isYouTubeShorts(iframe)) {
              ratio = "9 / 16";
              isVertical = true;
            }

            iframe.style.display = "block";
            iframe.style.margin = "20px auto";
            iframe.style.border = "0";
            iframe.style.width = "100%";

            if (isVertical) {
              iframe.style.maxWidth = "420px";
            } else {
              iframe.style.maxWidth = "100%";
            }

            if ("aspectRatio" in document.documentElement.style) {
              iframe.style.aspectRatio = ratio;
              iframe.style.height = "auto";
            } else {
              // fallback for old browsers
              const wrapper = document.createElement("div");
              wrapper.style.position = "relative";
              wrapper.style.width = "100%";
              wrapper.style.maxWidth = isVertical ? "420px" : "100%";
              wrapper.style.margin = "20px auto";
              wrapper.style.paddingTop = isVertical ? "177.77%" : "56.25%";

              iframe.style.position = "absolute";
              iframe.style.top = "0";
              iframe.style.left = "0";
              iframe.style.width = "100%";
              iframe.style.height = "100%";

              iframe.parentNode.insertBefore(wrapper, iframe);
              wrapper.appendChild(iframe);
            }

            iframe.removeAttribute("width");
            iframe.removeAttribute("height");
          }

          function addAspectRatioToEmbeds(root = document) {

            const embeds = root.querySelectorAll(`
              iframe[src*="youtube"],
              iframe[src*="youtu.be"],
              iframe[src*="facebook.com"],
              iframe[src*="instagram.com"],
              iframe[src*="x.com"],
              iframe[src*="twitter.com"],
              iframe[src*="drive.google.com"]
            `);

            embeds.forEach(fixVideoAspectRatio);
          }

          document.addEventListener("DOMContentLoaded", function () {

            addAspectRatioToEmbeds();

            const container = document.querySelector(".blog-posts") || document.body;

            const observer = new MutationObserver(function (mutations) {
              for (const mutation of mutations) {
                for (const node of mutation.addedNodes) {
                  if (node.nodeType === 1) {
                    if (node.tagName === "IFRAME") {
                      fixVideoAspectRatio(node);
                    } else {
                      addAspectRatioToEmbeds(node);
                    }
                  }
                }
              }
            });

            observer.observe(container, {
              childList: true,
              subtree: true,
            });

          });

