          document.addEventListener("DOMContentLoaded", function () {

            const embeddedUrls = new Set(); // prevent duplicate across page
            const comments = document.querySelectorAll(".comment-content");

            comments.forEach(function(comment) {

              let embedCreated = false; // limit 1 per comment

              comment.querySelectorAll("a").forEach(function(link) {

                if (embedCreated) return;

                const href = link.getAttribute("href");
                if (!href) return;

                const socialPattern = /facebook\.com|instagram\.com|twitter\.com|x\.com|pinterest\.com/i;
                if (!socialPattern.test(href)) return;

                if (embeddedUrls.has(href)) return;

                embedCreated = true;
                embeddedUrls.add(href);

                fetch("https://api.microlink.io/?url=" + encodeURIComponent(href))
                  .then(res => res.json())
                  .then(data => {

                    const title = data.data.title || href;
                    const description = data.data.description || "";
                    const image = data.data.image?.url || "";
                    const domain = new URL(href).hostname.replace("www.", "");

                    const card = document.createElement("div");
                    card.className = "tg-preview-card";

                    card.innerHTML = `
                      ${image ? `<div class="tg-thumb" style="background-image:url('${image}')"></div>` : ""}
                      <div class="tg-info">
                        <div class="tg-title">${title}</div>
                        <div class="tg-desc">${description}</div>
                        <div class="tg-domain">${domain}</div>
                      </div>
                    `;

                    card.addEventListener("click", function() {

                      if (window.innerWidth <= 768) {
                        window.location.href = href;
                        return;
                      }

                      let width = 800;
                      let height = 700;

                      if (/facebook\.com/i.test(href)) { width = 900; height = 700; }
                      else if (/instagram\.com/i.test(href)) { width = 500; height = 750; }
                      else if (/twitter\.com|x\.com/i.test(href)) { width = 600; height = 750; }
                      else if (/pinterest\.com/i.test(href)) { width = 850; height = 650; }

                      const left = (screen.width - width) / 2;
                      const top = (screen.height - height) / 2;

                      const popup = window.open(
                        href,
                        "_blank",
                        `width=${width},height=${height},top=${top},left=${left},resizable=yes,scrollbars=yes`
                      );

                      if (!popup) window.open(href, "_blank");

                    });

                    link.replaceWith(card);

                  })
                  .catch(() => {});
              });

            });
          });
