        (function () {

          function parseDate(dateStr) {
            // "11/24/2025 04:31:00 pm"
            const m = dateStr.match(/(\d+)\/(\d+)\/(\d+) (\d+):(\d+):(\d+) (am|pm)/i);
            if (!m) return null;

            let month = parseInt(m[1], 10) - 1;
            let day = parseInt(m[2], 10);
            let year = parseInt(m[3], 10);
            let hour = parseInt(m[4], 10);
            let minute = parseInt(m[5], 10);
            let second = parseInt(m[6], 10);
            let ampm = m[7].toLowerCase();

            if (ampm === "pm" && hour < 12) hour += 12;
            if (ampm === "am" && hour === 12) hour = 0;

            return new Date(year, month, day, hour, minute, second);
          }

          function formatFullDate(date) {
            return date.toLocaleDateString(undefined, {
              year: "numeric",
              month: "long",
              day: "numeric"
            });
          }

          function plural(value, unit) {
            return value + " " + unit + (value === 1 ? "" : "s") + " ago";
          }

          function timeAgoOrDate(date) {
            const seconds = Math.floor((Date.now() - date.getTime()) / 1000);
            const days = Math.floor(seconds / 86400);

            //  Older than 1 year  full date
            if (days >= 365) {
              return formatFullDate(date);
            }

            if (seconds < 60) return "Just now";

            const minutes = Math.floor(seconds / 60);
            if (minutes < 60) return plural(minutes, "minute");

            const hours = Math.floor(minutes / 60);
            if (hours < 24) return plural(hours, "hour");

            if (days < 7) return plural(days, "day");

            const weeks = Math.floor(days / 7);
            if (weeks < 4) return plural(weeks, "week");

            const months = Math.floor(days / 30);
            return plural(months, "month");
          }

          function convert(context) {
            (context || document)
              .querySelectorAll("time.published:not([data-converted])")
              .forEach(function (el) {
                const date = parseDate(el.textContent.trim());
                if (date) {
                  el.textContent = timeAgoOrDate(date);
                  el.setAttribute("data-converted", "true");
                }
              });
          }

          // Initial run
          document.addEventListener("DOMContentLoaded", function () {
            convert(document);
          });

          //  Infinite scroll support
          const observer = new MutationObserver(function (mutations) {
            mutations.forEach(function (m) {
              m.addedNodes.forEach(function (node) {
                if (node.nodeType === 1) convert(node);
              });
            });
          });

          observer.observe(document.querySelector('.blog-posts'),  {
            childList: true,
            subtree: true
          });

        })();

